Main={toggle_nav:function(e){var nav=$('nav');if(nav.is(':visible')){nav.animate({opacity:0},500,function(){nav.hide();});}else{nav.show().animate({opacity:1},500);}},prev_portfolio_img:function(e){var self=$(this),img=self.next().find('img'),src=img.attr('src'),src_segments=src.split('screenshot_'),target=src_segments[src_segments.length-1].split('.')[0];number=--target;img.attr('src',src_segments[0]+'/screenshot'+number+'.png');},next_portfolio_img:function(e){var self=$(this),img=self.prev().find('img'),src=img.attr('src'),src_segments=src.split('screenshot_'),target=src_segments[src_segments.length-1].split('.')[0];number=++target;img.attr('src',src_segments[0]+'/screenshot'+number+'.png');},init:function(){$('.burger_icon, .close_nav, nav a').click(this.toggle_nav);$('#home #portfolio .piece .arrow.left_arrow').click(this.prev_portfolio_img);$('#home #portfolio .piece .arrow.right_arrow').click(this.next_portfolio_img);}}
$(document).ready(function(){Main.init();});
