{var local=true;for(var i=host_segments.length-1;i>=0;i--){if(isNaN(host_segments[i])){local=false;}};if(local){return'http://'+window.location.hostname+'/textingbase_dev/public/'+url;}else{return window.location.origin+'/'+url;}}
